mapper.select:
select med_rd_no , pt_name , ge_code, ge_name, visit_dept_name, visit_dept_code, med_class_name, med_class_code, visit_time, visit_finish_time, pt_head_pic, pay_time , status_code from inter_hosp_order a where
and ( a.pt_name like "%"#{queryStr}"%" or a.diseases_name like "%"#{queryStr}"%" )
order by create_time desc limit ${pageNum}, ${pageSize}

select
count(1)
from
inter_hosp_order a
where


    and a.visit_doc_id = #{docId}


        and a.visit_time >= #{startTime} 


       and a.visit_time <= #{endTime} 


    and a.med_class_code = #{serviceCode}


    and a.status_code = #{status}


      and ( a.pt_name like "%"#{queryStr}"%"
      or a.diseases_name like "%"#{queryStr}"%" )
